
// Mental Model:
// f(str, 0, length-1) = OR{ f(str[0, i]) AND f(str[i, length-1]) }   i=1..length-2

function wordBreak(str, wordDict) {
  let dict = {}
  wordDict.forEach(w => dict[w] = true);

  return wordBreakHelper(str, dict)
}

function wordBreakHelper(str, dict, cache={}) {
  let start = 0;
  let end = str.length;

  if (dict[str]) {
    cache[str] = true;
    return true
  }

  for (let i = 1; i < end; i++) {
    let str1 = str.slice(0, i);
    let str2 = str.slice(i, end);

    let sub1 = cache[str1] !== undefined ? cache[str1] : wordBreakHelper(str1, dict, cache)
    let sub2 = cache[str2] !== undefined ? cache[str2] : wordBreakHelper(str2, dict, cache)

    if (sub1 && sub2) {

      cache[str] = true
      return true
    }
  }

  cache[str] = false
  return false
}







